/**
 * https://en.wikipedia.org/wiki/CIE_1960_color_space
 *
 * Obsolete color space
 *
 * @module  color-space/ucs
 */

'use strict';

const xyz = require('./xyz');

const ucs = module.exports = {
  name: 'ucs',
  min: [0, 0, 0],
  max: [100, 100, 100],
  channel: ['U', 'V', 'W'],
  alias: ['UCS', 'cie1960'],
};


/**
 * UCS to XYZ
 *
 * @param {Array} ucs XYZ values
 *
 * @return {Array} UCS values
 */
ucs.xyz = function(ucs) {
  const u = ucs[0];
  const v = ucs[1];
  const w = ucs[2];

  return [
    1.5 * u,
    v,
    1.5 * u - 3 * v + 2 * w,
  ];
};


/**
 * XYZ to UCS
 *
 * @param {Array} ucs UCS values
 *
 * @return {Array} XYZ values
 */
xyz.ucs = function(xyz) {
  const x = xyz[0];
  const y = xyz[1];
  const z = xyz[2];

  return [
    x * 2 / 3,
    y,
    0.5 * (-x + 3 * y + z),
  ];
};
