/**
 * @module  mumath/closest
 */

'use strict';

module.exports = function closest(num, arr) {
	let curr = arr[0];
	let diff = Math.abs(num - curr);
	for (let val = 0; val < arr.length; val++) {
		const newdiff = Math.abs(num - arr[val]);
		if (newdiff < diff) {
			diff = newdiff;
			curr = arr[val];
		}
	}
	return curr;
}
